	static int atomic = 0;;

	public static String getHostName() {
		InetAddress ipAddr;
		try {
			ipAddr = InetAddress.getLocalHost();
			return ipAddr.getHostName();
		} catch (UnknownHostException e) {
			e.printStackTrace();
		}
		return "localhost";
	}

	/***
	 * 	
	 */
	public static synchronized void startBanner() {
		try {
			File htmlFile = Paths.get("start.html").toFile();
			if (atomic == 0 && htmlFile.exists()) {
				atomic=1;//run once
				Desktop.getDesktop().browse(htmlFile.toURI());
			} else {
				String newData = getStartTemplate();
				AppUtil.writeFile(newData, htmlFile.getPath());

			}
		} catch (Exception e) {
		}
	}

