    /***
     * @param rdto {@link ReportsDto}
     * @return {@link String}
     */
	public static String prepareMailBody(CommonDto rdto) {
		StringBuilder sb = new StringBuilder(AppUtil.EMPTY_STR);
		sb.append("<table border='1' cellpadding='10' style='border: 1px solid #000080;' width='100%'><tr bgcolor='#A9A9A9'>")
				.append("<td> TEST NAME </td>")
				.append("<td> ENV URL </td> ");
				if(rdto.getJiraTicket()!=null && !rdto.getJiraTicket().isEmpty()) {
				   sb.append("<td> JIRA TICKET </td> ");
				 }
				if(rdto.getConfluenceUrl()!=null && !rdto.getConfluenceUrl().isEmpty()) {
				   sb.append( "<td> CONFLUENCE URL </td> ");
				}
				sb.append("<td> SPRINT </td> ")
				.append("<td> USER&nbsp;STORY </td> ")
				.append("<td>PASS</td>")
				.append("<td>FAIL</td>")
				.append("<td>TOTAL</td>")
				.append("<td>STAUS</td>")
				.append("</tr>");
		
		        sb.append("<tr>");
				sb.append("<td>").append(rdto.getTcName()).append("</td>");
				sb.append("<td>").append(rdto.getEnvUrl()).append("</td>");
				if(rdto.getJiraTicket()!=null && !rdto.getJiraTicket().isEmpty()) {
				  sb.append("<td>").append(rdto.getJiraTicket()).append("</td>");
				}
				if(rdto.getConfluenceUrl()!=null && !rdto.getConfluenceUrl().isEmpty()) {
				   sb.append("<td>").append(rdto.getConfluenceUrl()).append("</td>");
				}
				sb.append("<td>").append(rdto.getTcVersion()).append("</td>");
				sb.append("<td>").append(rdto.getTcStory()).append("</td>");
				sb.append("<td>").append(rdto.getPassSteps()).append("</td>");
				sb.append("<td>").append(rdto.getFailSteps()).append("</td>");
				sb.append("<td>").append(rdto.getTotalSteps()).append("</td>");
				String color="PASS".equalsIgnoreCase(rdto.getRunStatus())?"<span style='background-color:green'>PASS</span>":"<span style='background-color:red'>FAIL</span>";
				sb.append("<td>").append(color).append("</td>");

		sb.append("</tr>");
		sb.append("</table>");
		sb.append("<p></p>");
		return sb.toString();
	}
	

